import React from 'react';

const WellnessSummary = ({ delay = 0 }) => {
  const summaryInsights = [
    "Edna took her medication yesterday but hasn't taken it yet today â€” a gentle reminder might help.",
    "She last ate lunch at 1:02 PM and completed her morning walk at 8:30 AM.",
    "She had a video call with her daughter this morning, keeping her emotionally connected.",
    "Overall, Nomi interprets today as a balanced and healthy day â€” calm vitals, stable activity, and good mood indicators."
  ];

  return (
    <div 
      className="max-w-4xl mx-auto mb-12 px-6 animate-fade-in"
      style={{ animationDelay: `${delay}ms` }}
    >
      <div className="bg-white rounded-2xl shadow-lg p-8 border border-gray-100">
        {/* Header */}
        <div className="flex items-center mb-6">
          <div className="text-3xl mr-4">ðŸ§ </div>
          <h2 className="text-2xl font-bold text-gray-800">Wellness Summary</h2>
        </div>

        {/* LLM Generated Summary */}
        <div className="space-y-4">
          <div className="flex items-center mb-4">
            <div className="text-sm font-medium text-gray-500 uppercase tracking-wide">
              Generated by Nomi's LLM Wellness Engine
            </div>
          </div>
          
          <div className="space-y-4">
            {summaryInsights.map((insight, index) => (
              <p 
                key={index}
                className="text-gray-700 leading-relaxed text-lg"
              >
                {insight}
              </p>
            ))}
          </div>
        </div>

        {/* Additional Context */}
        <div className="mt-8 p-6 bg-gradient-to-r from-blue-50 to-indigo-50 rounded-xl border border-blue-100">
          <div className="flex items-center mb-3">
            <div className="text-xl mr-2">ðŸ’¡</div>
            <h3 className="text-lg font-semibold text-blue-800">Nomi's Recommendation</h3>
          </div>
          <p className="text-blue-700 leading-relaxed">
            "Based on today's patterns, consider a gentle medication reminder and perhaps a light evening walk 
            to maintain Edna's excellent wellness momentum. Her emotional connections are strong â€” 
            this social engagement is contributing positively to her overall health."
          </p>
        </div>
      </div>
    </div>
  );
};

export default WellnessSummary;
